import React, { useState } from "react";

const App = () => {
    const [myArray, setMyArray] = useState([
        {
          name: "nima",
          age: 26,
          email: "nima@nima.com",
          id: "1",
        },
        {
          name: "jacob",
          age: 16,
          email: "jacob@jacob.com",
          id: "2",
        },
        {
          name: "lale",
          age: 24,
          email: "lale@lale.com",
          id: "3",
        },
      ])
  const deleteHandler = (id) => {
      const updateArray = myArray.filter((item) => item.id !== id);
      setMyArray(updateArray)
  };
  const [value, setValue] = useState(0)
  const clickHandler = (type) => {
    if(type === "increase") setValue(value + 1);
    else {
      if(value > 0) setValue(value - 1);
       else alert("impossible!!")
    }

  }
  return (
    <>
      <table style={{ margin: "10px auto", width: "70%", textAlign: "center" }}>
        <tr>
          <th>id</th>
          <th>Name</th>
          <th>Age</th>
          <th>Email</th>
          <th>Delete button</th>
        </tr>

        {myArray.map(({id, name, age, email}) => {
          return (
            <tr>
              <td>{id}</td>
              <td>{name}</td>
              <td>{age}</td>
              <td>{email}</td>
              <td>
                <button onClick={() => deleteHandler(id)}>delete</button>
              </td>
            </tr>
          );
        })}
      </table>
      <div style={{margin: 'auto'}}>
         <button onClick={() => clickHandler("increase")}>+</button>
            <span>{value}</span>
         <button onClick={() => clickHandler("decrease")}>-</button>
      </div>
    </>
  );
};

export default App;
