// Profile.js
import React, { useEffect } from "react";
import { useLocation } from "react-router-dom";
import "../index.css";
import "../component/Form.css";

const Profile = () => {
  const location = useLocation();
  const { username } = location.state;

  useEffect(() => {
    // Populate the username input field with the value stored in localStorage
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      document.getElementById("usernameInput").value = storedUsername;
    }
  }, []);

  const handleEdit = (e) => {
    e.preventDefault();

    // Update username and password in localStorage
    const newUsername = e.target.elements.username.value;
    const newPassword = e.target.elements.password.value;
    localStorage.setItem("username", newUsername);
    localStorage.setItem("password", newPassword);

    // Clear input fields
    e.target.elements.username.value = "";
    e.target.elements.password.value = "";
  };

  return (
    <form className="form-container" style={{ marginTop: "50px" }} onSubmit={handleEdit}>
      <p>profile</p>
      <input type="text" name="username" id="usernameInput" defaultValue={username} />
      <input type="password" name="password" />
      <button type="submit">edit</button>
    </form>
  );
};

export default Profile;
